package RelationalOperators;

public class RelationalOperations2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		
		System.out.println("a equals to b: "+(a==b));
		System.out.println("a is smaller than b: "+(a<b));
		System.out.println("a is bigger than b: "+(a>b));
		System.out.println("a is smaller or equal to b: "+(a<=b));
		System.out.println("a is greater or equal to b: "+(a>=b));
		
		// a! stands for not equal
		System.out.println("a is not equal to b: "+(a!=b));

	}

}
