package RelationalOperators;

public class RelationalOperator1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=10;
		int b=20;
		
		System.out.println(a==b);
		System.out.println(a<b);
		System.out.println(a<b);
		System.out.println(a<=b);
		System.out.println(a>=b);
		
		// a! stands for not equal
		System.out.println(a!=b);
		
	
		
		
		//Relational Operators give the answer in boolean data type either false or true
		//for Equal to, you have to use 2 equal sign ==
	}

}
